function WP_GP_PP_initGIFCanvas(){const e=document.querySelectorAll(".wp-gp-pp-gif-canvas-player");for(let n=0;n<e.length;n++){const t=e[n].nextElementSibling,i=new SuperGif({gif:e[n]});i.load(()=>WP_GP_PP_toggleCanvasGIF(t,i))}}function WP_GP_PP_toggleCanvasGIF(e,n){n.get_auto_play()||n.pause(),e.onclick=()=>{const t=e.children[0],i=n.get_playing();i?t.classList.remove("is-playing"):t.classList.add("is-playing"),i?n.pause():n.play()}}function WP_GP_PP_toggleVideosGIF(){const e=document.querySelectorAll(".wp-gp-pp-video-player");for(let n=0;n<e.length;n++){const t=e[n],i=t.nextElementSibling;i.onclick=()=>{const e=i.children[0].classList,n=e.contains("is-playing");n?e.remove("is-playing"):e.add("is-playing"),n?t.pause():t.play()}}}function WP_GP_PP_toggleGIF(){const e=document.querySelectorAll(".wp-gp-pp-gif-thumbnail");for(let n=0;n<e.length;n++){const t=e[n],i=t.parentElement,l=t.nextElementSibling.nextElementSibling;l.onclick=()=>{const e=l.children[0].classList,n=e.contains("is-playing");let s=document.getElementById(t.id.replace("--thumbnail",""));if(n?e.remove("is-playing"):e.add("is-playing"),""===s.getAttribute("src")){let e=t.getAttribute("src").replace("_gif_thumbnail.jpeg",".gif");s.setAttribute("src",e)}n?i.classList.remove("is-playing"):i.classList.add("is-playing")}}}function WP_GP_PP_INIT(){const e=WP_GIF_PLAYER.gifPlayersInPost;e.includes("gif")&&WP_GP_PP_toggleGIF(),e.includes("canvas")&&WP_GP_PP_initGIFCanvas(),e.includes("video")&&WP_GP_PP_toggleVideosGIF()}document.addEventListener("DOMContentLoaded",WP_GP_PP_INIT);